import React, { useState, useEffect } from 'react'
import Sidebar from "../Component/Sidebar"
const Header = () => {
    const [adScroll, setAdScroll] = useState(false);
    const [opnAbout, setOpnAbout] = useState(false);
    const MyScroll =()=>{
        if (window.pageYOffset > 2) {
            setAdScroll(true)
        }else{
            setAdScroll(false)
        }
    }
    useEffect(() => {
        window.addEventListener("scroll", MyScroll);
        return () => {
            window.addEventListener("scroll", MyScroll);
        }
    }, [])

    const opnAboutEvent =(e)=>{
        if (opnAbout === false) {
            setOpnAbout(true)
        }else{
            setOpnAbout(false)
        }
       
    }
    const CloseEvent =()=>{
        setOpnAbout(false)
    }
    return (
        <>
            <header> 
                <div className={adScroll ? "main_menu scroll" : "main_menu" }
                 
                 >
                    <div className= "center_menu">
                        <div className="logo">
                            <a href="/">
                                <span className="brand">SK Technical</span>
                            </a>
                        </div>
                        <span  className="plus_menu" onClick={opnAboutEvent}></span>
                        <div className="main_hmbrgr">
                            <div className="hamburger_menu">
                                <div className="line1"></div>
                                <div className="line2"></div>
                                <div className="line3"></div>
                            </div>
                        </div>
                    </div>
                </div>
            </header>
            {/* ///////////////////////////// */}
           <Sidebar AbEvent={opnAboutEvent}/>
           {
               Slidebar
           }
        </>
    )
}

export default Header
