import React, { useState, useEffect } from 'react'
// import Sidebar from "../Component/Sidebar"
const Header = () => {
    const [adScroll, setAdScroll] = useState(false);
    const MyScroll =()=>{
        if (window.pageYOffset > 2) {
            setAdScroll(true)
        }else{
            setAdScroll(false)
        }
    }
    useEffect(() => {
        window.addEventListener("scroll", MyScroll);
        return () => {
            window.addEventListener("scroll", MyScroll);
        }
    }, [])

    const opnAboutEvent =()=>{
     const aboutData = document.getElementsByClassName("slides");
     aboutData.classList.add("active")
     alert("open")
    }

    return (
        <>
            <header>
                <div className="main_menu">
                    <div className={adScroll ? "center_menu scroll" : "center_menu"}>
                        <div className="logo">
                            <a href="/">
                                <span className="brand">SK Technical</span>
                            </a>
                        </div>
                        <span  className="plus_menu" onClick={opnAboutEvent}></span>
                        <div className="main_hmbrgr">
                            <div className="hamburger_menu">
                                <div className="line1"></div>
                                <div className="line2"></div>
                                <div className="line3"></div>
                            </div>
                        </div>
                    </div>
                </div>
            </header>
            {/* ///////////////////////////// */}
            <div className="slibar">
           
              <div className="slides">
              <span className={opnAbout ? "slides active" : "slides"}>
              <i class="fas fa-times" aria-hidden="true"></i>
              </span>
                  <div className="plus_about">
                  <h3 className="plus_heading">
                  ABOUT MY WORK
                  </h3>
                 <p>I'm full-stack developer having expertise in React Js/Redux + Node Js, Express Js, MongoDB and Firestore. Strong team builder, having a high level of personal morals, My skills also include AWS, Google Cloud, Firebase, Digital Ocean, WordPress, Woo-commerce, etc.</p>
                  </div>
              </div>
          </div>
        </>
    )
}

export default Header
