import React, { useState } from 'react'
// import img from "./image/b2.jpg"

const App = () => {
  // const [input, setInput] = useState("")
  const [name, setName] = useState("")

  const changeEvent =(e)=>{
    setInput(e.target.value)

  }
  const inputEvent =(e)=>{
    e.preventDefault()
    setName(input)
    setInput("")
  }
  
  return (
    <>
    <h1>{name}</h1>
    <form onSubmit={inputEvent}>

    <input type="text" 
      placeholder="Enter Your Name"
      value={input}
      onChange={changeEvent}
    />
    <input type="submit"  />
    </form>
        
    </>
  )
}

export default App
