import React, { useState, useEffect } from 'react'
import Sidebar from "../Component/Sidebar"
// import Menu from "../Component/Sidebar"
const Header = () => {
    const [adScroll, setAdScroll] = useState(false);
    const [opnAbout, setOpnAbout] = useState(false);
    const [menuShow, setMenuShow] = useState(false);
    const MyScroll =()=>{
        if (window.pageYOffset > 2) {
            setAdScroll(true)
        }else{
            setAdScroll(false)
        }
    }
    useEffect(() => {
        window.addEventListener("scroll", MyScroll);
        return () => {
            window.addEventListener("scroll", MyScroll);
        }
    }, [])

    const opnAboutEvent =()=>{
      
        if (opnAbout === false) {
            setOpnAbout(true)
        }else{
            setOpnAbout(false)
        }

    }
    const CloseEvent =()=>{
        setOpnAbout(false)
    }

    const openMenu=()=>{
        if (menuShow === false) {
            setOpnAbout(true)
        }else{
            setOpnAbout(false)
        }

    }
    return (
        <>
            <header> 
                <div className={adScroll ? "main_menu scroll" : "main_menu" }       
                 >
                    <div className= "center_menu">
                        <div className="logo">
                            <a href="/">
                                <span className="brand">SK Technical</span>
                            </a>
                        </div>
                        {/* {opnAbout ? " +" : "-"} */}
                        <span  className="plus_menu" onClick={opnAboutEvent}>
                        {opnAbout ? " -" : " +"} 
                        </span>
                        <div className="main_hmbrgr" onClick={openMenu}>
                            <div className="hamburger_menu" >
                                <div className="line1"></div>
                                <div className="line2"></div>
                                <div className="line3"></div>
                            </div>
                        </div>
                    </div>
                </div>
            </header>
            {/* {Sidebar section } */}
           <Sidebar opnAbout={opnAbout}
               opnAboutEvent={opnAboutEvent}
               CloseEvent={CloseEvent}
           />
           {/* ////////////////////// */}
           
           <div className= {menuShow ? "menu showMenu" : "menu"}>
                <div className="menues">
                    <div className="menu_link">
                        <a href="">Home</a>
                        <a href="">Portfolio</a>
                        <a href="">Articles</a>
                        <a href="">Let's Talk</a>
                    </div>
                </div>
            </div>
        
        </>
    )
}

export default Header
