import React, { useState } from 'react'

import { Button, Spinner } from 'react-bootstrap'
import 'bootstrap/dist/css/bootstrap.min.css';


const App = () => {
  const [loading, setLoading] = useState(false)
  
  return (
    <>
     {

      loading ?
     <div>
                <Button variant="dark" disabled on >
                    <Spinner
                    as="span"
                    variant="light"
                    size="sm"
                    role="status"
                    aria-hidden="true"
                    animation="border"/>
                      Loading...
                </Button>
            </div> : null
          }
    </>
  );
}

export default App;
