import React, { useState } from 'react'
// import Todolist from "./Todolist"
// import img from "./image/b2.jpg"

const App = () => {
  const [data, setData] = useState("")
  const [move, setMove] = useState([])
  const changeEvent = (e) => {
    setData(e.target.value)
  }
  const adItem = () => {
    setMove((preVal) => {
      return [...preVal, data]
    })
    setData([])
  }

  const dltItem = () => {
    setMove((oldVal) => {
      return oldVal.filter((ary, index) => {
        return index !== id;
      })
    })
  }

  return (
    <>
      <h1>To DoList</h1>
      <input type="text"
        placeholder="Add a items"
        onChange={changeEvent}
        value={data}
      />
      <button onClick={adItem}>+</button>
      <ol>
        {
          move.map((crnVal, index) => {
            return <>
            <button
             key={index}
                id={index}
            onClick={dltItem}>-</button>  
       <li>{move}</li>
              {/* <Todolist
                key={index}
                id={index}
                move={crnVal}
                onClick={dltItem} */}
              {/* /> */}
            </>
          })
        }
      </ol>

    </>
  )
}

export default App
