import React,{useState, useEffect} from 'react'

const Footer = () => {
    const [menuShow, setMenuShow] = useState();
    const MyScroll = () => {
        if (window.scrollY > 800) {
            setMenuShow(-5)
        } else {
            setMenuShow()
        }
    }
    useEffect(() => {
        window.addEventListener("scroll", MyScroll);
        return () => {
            window.addEventListener("scroll", MyScroll);
        }
    }, [])
    return (
        <>
            <footer style={{zIndex: menuShow}}>
                <div classNameName="footer_container">
                    <p>
                        Â© Copyright 2014 - 2021 &nbsp; | &nbsp; All Rights Reserved &nbsp; | &nbsp; Developed by&nbsp;&nbsp;
                        <a href="/">
                            SK Technical
                        </a>
                    </p>
            <div className="social-links">
                        <a href="/" target="_blank" rel="noreferrer">
                            <span className="tooltip">linkedIn
                            </span>
                            <i className="fab fa-linkedin-in" aria-hidden="true"></i>
                         </a>
                        <a href="/" target="_blank" rel="noreferrer">
                        <span className="tooltip"> facebook</span>
                            <i className="fab fa-facebook-f" aria-hidden="true"></i></a>
                        <a href="/" target="_blank" rel="noreferrer">
                        <span className="tooltip">twitter</span>
                            <i className="fab fa-twitter" aria-hidden="true"></i></a>
                        <a href="/" target="_blank" rel="noreferrer">
                        <span className="tooltip">instagram</span>
                            <i className="fab fa-instagram" aria-hidden="true"></i>
                        </a>
              </div>                
                </div>
            </footer>
        </>
    )
}

export default Footer
