import React, { useState } from 'react'

const ContactForm = () => {
    const [form,setform] = useState()
    const [inputData, setInputDaa] = useState({
        name: "",
        gmail: "",
        phone: ""
    })
    const inputEvent = (event) => {
        const { name, Value } = event.target
        setInputDaa((preVal)=>{
            return{
                preVal,
                [name] : Value,
            }
        })
    }

    const formSubmit =(e)=>{
        e.preventDefault();
        setform(()=>{
            const data = document.getElementsByClassName("contact_form")
              data.classList.add("success").innerHTML = "Got it, I'll contact you shortly, Thank";
        })
        
    }
    return (
        <>
            <div className="contact_form">
                <form action="" onSubmit={formSubmit}>
                    <input type="text"
                        name="name"
                        placeholder="Name" required
                        value={inputData.name}
                        onChange={inputEvent}
                    />
                    <input type="gmail"
                        name="gmail"
                        placeholder="Gmail" required
                        value={inputData.gmail}
                        onChange={inputEvent}
                    />
                    <input type="numpber"
                        name="phone"
                        placeholder="Phone" required
                        value={inputData.phone}
                        onChange={inputEvent}
                    />
                    <button type="submit">&nbsp;LET'S TALK
                    <p className="ptn_pera"></p> </button>
                </form>
            </div>
        </>
    )
}

export default ContactForm
