import React, { useState } from 'react'
// import img from "./image/b2.jpg"

const App = () => {
  const [data, setData] = useState("")
  const [move, setMove] = useState([])
const changeEvent =(e)=>{
  setData(e.target.value)
}
const adItem =()=>{
  setMove((preVal)=>{
    return[...preVal, data]
  })
  setData([])
}

const dltItem =(id)=>{
  setMove((oldVal)=>{
    return oldVal.filter((ary, index)=>{
      return id !== index
    })
  })
}
  
  return (
    <>
   <h1>To DoList</h1>
   <input type="text" 
     placeholder="Add a items"
     onChange={changeEvent}
     value={data}
   />
   <button onClick={adItem}>+</button>
   <ol>
     {
      move.map((crnVal, index)=>{
       return<>
   <button onClick={dltItem}>-</button>
        {/* id={index} */}
       <li>{crnVal}</li>
       </> 
      })
     }
   </ol>
        
    </>
  )
}

export default App
